curl --location 'https://api.altrum.ai/v1/openai/chat/completions' --header 'Authorization: Bearer OPENAI_API_KEY' --header 'Project-Api-Key: ALTRUMAI_PROJECT_API_KEY' --header 'Content-Type: application/json' --data '{
    "model":"gpt-4o",
    "messages": [
        {"role":"assistant", "content":"You are a helpful assistant"},
        {"role":"user", "content":"Hello!"}
    ],
    "stream":false
}'

const { OpenAI } = require("openai");

const openai = new OpenAI({
    baseURL: "https://api.altrum.ai/v1/openai/",
    apiKey: "OPENAI_API_KEY",
    defaultHeaders: {"Project-Api-Key": "ALTRUMAI_PROJECT_API_KEY"}
    });

(async () => {
const completion = await openai.chat.completions.create({
    model: "gpt-4o-mini",
    messages: [
    { role: "assistant", content: "You are a helpful assistant" },
    { role: "user", content: "Hello!" }
    ],
    stream: false
});
console.log(completion.choices[0].message.content);
})();

